// Transformation depensesante
val depensesante = spark.read.parquet("abfss://ocde@groupeocde.dfs.core.windows.net/bronze/depensesante")

// SUPPRESSION COLONNES + FILTRE PAYS ETUDIES
val depensesante_supp_colonnes = depensesante.drop("HF","HC","HP","MEASURE")
val depensesante_filtre_pays = depensesante_supp_colonnes.filter(depensesante_supp_colonnes("LOCATION").isin(listepays:_*))
val depensesante_clean = depensesante_filtre_pays.withColumnRenamed("value","DEPENSE_SANTE_%_PIB").join(population_totale_clean, Seq("LOCATION","TIME_PERIOD"), "inner").withColumnRenamed("LOCATION","PAYS").withColumnRenamed("TIME_PERIOD","ANNEE").orderBy("PAYS","ANNEE")

// ENREGISTREMENT .PARQUET => ARGENT
depensesante_clean.write.mode("overwrite").parquet("abfss://ocde@groupeocde.dfs.core.windows.net/argent/depensesante")

// Transformation cotisationsecu
val cotisationsecu = spark.read.parquet("abfss://ocde@groupeocde.dfs.core.windows.net/bronze/cotisationsecu")

// SUPPRESSION COLONNES + FILTRE PAYS ETUDIES
val cotisationsecu_supp_colonnes = cotisationsecu.drop("GOV","TAX","VAR")
val cotisationsecu_filtre_pays = cotisationsecu_supp_colonnes.filter(cotisationsecu_supp_colonnes("COU").isin(listepays:_*))
val cotisationsecu_clean = cotisationsecu_filtre_pays.withColumnRenamed("value","COTISATION_SECU(%PIB)").withColumnRenamed("COU","PAYS").withColumnRenamed("TIME_PERIOD","ANNEE").orderBy("PAYS","ANNEE")

// ENREGISTREMENT .PARQUET => ARGENT
cotisationsecu_clean.write.mode("overwrite").parquet("abfss://ocde@groupeocde.dfs.core.windows.net/argent/cotisationsecu")
