// Transformation depensesante
val depensesante = spark.read.parquet("abfss://ocde@groupeocde.dfs.core.windows.net/bronze/depensesante")

// SUPPRESSION COLONNES + FILTRE PAYS ETUDIES
val depensesante_supp_colonnes = depensesante.drop("HF","HC","HP","MEASURE")
val depensesante_filtre_pays = depensesante_supp_colonnes.filter(depensesante_supp_colonnes("LOCATION").isin(listepays:_*))
val depensesante_clean = depensesante_filtre_pays.withColumnRenamed("value","DEPENSE_SANTE_%_PIB").orderBy("LOCATION","TIME_PERIOD")

// ENREGISTREMENT .PARQUET => ARGENT
depensesante_clean.write.mode("overwrite").parquet("abfss://ocde@groupeocde.dfs.core.windows.net/argent/depensesante")

// Transformation pib
val pib = spark.read.parquet("abfss://ocde@groupeocde.dfs.core.windows.net/bronze/pib")


// SUPPRESSION COLONNES + FILTRE PAYS ETUDIES
val pib_supp_colonnes = pib.drop("TRANSACT","MEASURE")
val pib_filtre_pays = pib_supp_colonnes.filter(pib_supp_colonnes("LOCATION").isin(listepays:_*))
val pib_clean = pib_filtre_pays.withColumnRenamed("value","PIB (Milliers Dollards)").orderBy("LOCATION","TIME_PERIOD")

// ENREGISTREMENT .PARQUET => ARGENT
pib_clean.write.mode("overwrite").parquet("abfss://ocde@groupeocde.dfs.core.windows.net/argent/pib")

// Transformation couverturesociale
val couverturesociale = spark.read.parquet("abfss://ocde@groupeocde.dfs.core.windows.net/bronze/couverturesociale")

// SUPPRESSION COLONNES + FILTRE PAYS ETUDIES
val couverturesociale_supp_colonnes = couverturesociale.drop("VAR","UNIT")
val couverturesociale_filtre_pays = couverturesociale_supp_colonnes.filter(couverturesociale_supp_colonnes("COU").isin(listepays:_*))
val couverturesociale_clean = couverturesociale_filtre_pays.withColumnRenamed("value","POPULATION_COUVERTE(%)").withColumnRenamed("COU","LOCATION").orderBy("LOCATION","TIME_PERIOD")

// ENREGISTREMENT .PARQUET => ARGENT
couverturesociale_clean.write.mode("overwrite").parquet("abfss://ocde@groupeocde.dfs.core.windows.net/argent/couverturesociale")

// Transformation cotisationsecu
val cotisationsecu = spark.read.parquet("abfss://ocde@groupeocde.dfs.core.windows.net/bronze/cotisationsecu")

// SUPPRESSION COLONNES + FILTRE PAYS ETUDIES
val cotisationsecu_supp_colonnes = cotisationsecu.drop("GOV","TAX","VAR")
val cotisationsecu_filtre_pays = cotisationsecu_supp_colonnes.filter(cotisationsecu_supp_colonnes("COU").isin(listepays:_*))
val cotisationsecu_clean = cotisationsecu_filtre_pays.withColumnRenamed("value","COTISATION_SECU(%PIB)").withColumnRenamed("COU","LOCATION").orderBy("LOCATION","TIME_PERIOD")

// ENREGISTREMENT .PARQUET => ARGENT
cotisationsecu_clean.write.mode("overwrite").parquet("abfss://ocde@groupeocde.dfs.core.windows.net/argent/cotisationsecu")
