import org.apache.spark.sql.DataFrame

def getCSVDataFrame(filePath: String): DataFrame = {
    val df = spark.read.option("header", "true").csv(filePath)
    return df
    }

//def writeParquetFile(df, mode, filePath):
//    df.write.mode(mode).csv(filePath)

def getFilePath(relativePath: String, fileName: String): String = {
    val accountName = "groupeocde"
    val containerName = "ocde"
    // Construction du chemin relatif
    val adlsPath0 = "abfss://" + containerName + "@" + accountName + ".dfs.core.windows.net/" + relativePath
    val adlsPath = adlsPath0 + fileName
    return adlsPath
}

// Récupération des fichiers 
val pathDepenseSante = getFilePath("test", "/Depensesante")
println(pathDepenseSante)

// Liste des pays retenus 
val listePaysEurope = List("AUT" ,"BEL", "CZE" ,"DNK", "FIN", "FRA", "DEU" ,"GRC", "HUN" ,"ISL", "IRL" ,"ITA", "LUX" ,"NLD","NOR", "POL", "PRT","SVK","ESP", "SWE" ,"CHE" ,"TUR", "GBR", "EST" ,"SVN", "LVA", "LTU")

// Dépense Santé
val DepenseSante = getCSVDataFrame(pathDepenseSante)
    .filter($"SUBJECT" === "TOT")                  // On sélectionne le montant total
    .filter($"LOCATION".isin(listePaysEurope:_*))  // On sélectionne les pays d'Europe 
    .filter($"TIME" >= 1999)                       // On sélectionne les années à partir de 1999

def taux_evol(df: DataFrame, variable: String, nom_du_taux: String): DataFrame = {
    for(i <- 1 to .count()){
        if df.loc[i, "Pays"] == df.loc[i-1, "Pays"]:
            df.loc[i, nom_du_taux] = round((df.loc[i, variable] - df.loc[i-1, variable])*100 / df.loc[i-1, variable], 1)

      }
}
